          <div>
            <label for="date">Date:</label>
            <input type="date" id="date" formControlName="date" />
          </div>
          <div>
            <label for="time">Time:</label>
            <input type="time" id="time" formControlName="time" />
          </div>
          <div>
            <label for="numberOfPeople">Number of People:</label>
            <input type="number" id="numberOfPeople" formControlName="numberOfPeople"/>
          </div>
          <div>
            <label for="additionalRequests">Additional Requests:</label>
            <textarea id="additionalRequests" formControlName="additionalRequests"></textarea>
          </div>
          <button type="submit">Reserve</button>
        </form>
        <p>{{ feedbackMessage }}</p>

        <h2>Make an order</h2>

        <div *ngFor="let menuItem of restaurant.menu">
            <h3>{{ menuItem.name }}</h3>
            <p>{{ menuItem.description }}</p>
            <p>Price: {{ menuItem.price }}</p>
            <input type="number" [(ngModel)]="menuItem.quantity" />
            <button (click)="addToCart(menuItem)">Add to cart</button>
        </div>

        <br>
        <h3>Cart</h3>

        <div *ngFor="let orderItem of cart">
            <p>{{ OrderItem.quantity }} x {{ OrderItem.name }} - {{ OrderItem.price }}$</p>
        <br>
