import { Component } from '@angular/core';
import { UserService } from '../user.service';

@Component({
  selector: 'app-pocetna',
  templateUrl: './pocetna.component.html',
  styleUrls: ['./pocetna.component.css']
})
export class PocetnaComponent {

  constructor(private userService: UserService)
  {

  }

  username: string = ""
  password: string = ""
  type: string = ""
  error: string = ""


  login()
  {
    if (this.username == "" || this.password == "" || this.tip == "")
    {
      this.error = "Unesite sve podatke!";
      return;
    }

    this.userService.login(this.username, this.password, this.tip).subscribe(data=>{
      if (data)
      {
        this.error = ""
        localStorage.setItem("user", JSON.stringify(data))
        this.router.navigate([data.tip]);
      }
      else
      {
        this.error = "Nepostojeci korisnik"
      }
    })
  }

}
