import { Component, OnInit } from '@angular/core';
import { ChartOptions } from 'chart.js';
import { Label } from 'ng2-charts';

@Component({
  selector: 'app-guests-per-day-chart',
  templateUrl: './guests-per-day-chart.component.html',
  styleUrls: ['./guests-per-day-chart.component.css']
})
export class GuestsPerDayChartComponent implements OnInit{

  public barChartOptions: ChartOptions = {
    responsive: true,
  };
  public barChartLabels: Label[] = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  public barChartType: string = 'bar';
  public barChartLegend = true;

  public barChartData: ChartDataSets[] = [
    { data: [0, 0, 0, 0, 0, 0, 0], label: 'Guests per day' }
  ];

  constructor() { }

  ngOnInit(): void {
    // Simulated data for demonstration (replace with actual data fetching logic)
    this.barChartData[0].data = [30, 50, 70, 60, 40, 80, 45];
  }
}
