import { Component, OnInit } from '@angular/core';
import { NgxChartsModule } from '@swimlane/ngx-charts';
import { Color, ScaleType } from '@swimlane/ngx-charts';
import { DefaultArcObject } from 'd3-shape';
import * as d3Scale from 'd3-scale';

@Component({
  selector: 'app-guests-per-day-chart',
  templateUrl: './guests-per-day-chart.component.html',
  styleUrls: ['./guests-per-day-chart.component.css']
})
export class GuestsPerDayChartComponent implements OnInit{

  mondayGuests: number = 30;
  tuesdayGuests: number = 50;
  wednesdayGuests: number = 70;
  thursdayGuests: number = 60;
  fridayGuests: number = 40;
  saturdayGuests: number = 80;
  sundayGuests: number = 45;
}
<div class="chart-container">
    <h2>Number of Guests per Day</h2>
    <div class="chart">
      <div class="bar" style="height: {{ tuesdayGuests }}px;">Tuesday</div>
      <div class="bar" style="height: {{ wednesdayGuests }}px;">Wednesday</div>
      <div class="bar" style="height: {{ thursdayGuests }}px;">Thursday</div>
      <div class="bar" style="height: {{ fridayGuests }}px;">Friday</div>
      <div class="bar" style="height: {{ saturdayGuests }}px;">Saturday</div>
      <div class="bar" style="height: {{ sundayGuests }}px;">Sunday</div>
    </div>
  </div>
