import { Component, OnInit } from '@angular/core';
import { NgxChartsModule } from '@swimlane/ngx-charts';
import { Color, ScaleType } from '@swimlane/ngx-charts';

@Component({
  selector: 'app-guests-per-day-chart',
  templateUrl: './guests-per-day-chart.component.html',
  styleUrls: ['./guests-per-day-chart.component.css']
})
export class GuestsPerDayChartComponent implements OnInit{

  data: any[] = [
    { name: 'Monday', value: 30 },
    { name: 'Tuesday', value: 50 },
    { name: 'Wednesday', value: 70 },
    { name: 'Thursday', value: 60 },
    { name: 'Friday', value: 40 },
    { name: 'Saturday', value: 80 },
    { name: 'Sunday', value: 45 }
  ];

  view: [number, number] = [600, 400];

  // options
  showXAxis = true;
  showYAxis = true;
  gradient = false;
  showLegend = true;
  showXAxisLabel = true;
  xAxisLabel = 'Day';
  showYAxisLabel = true;
  yAxisLabel = 'Guests';

  colorScheme: Color[] = [
    {
      name: 'Green',
      selectable: true,
      group: ScaleType.Ordinal,
      domain: ['#5AA454', '#A10A28', '#C7B42C', '#AAAAAA']
    }
  ];

  constructor() { }

  ngOnInit(): void {
  }

  onSelect(event: any): void {
    console.log(event);
  }
}
<ngx-charts-bar-vertical
  [view]="view"
  [scheme]="colorScheme"
  [results]="data"
  [gradient]="gradient"
  [xAxis]="showXAxis"
  [yAxis]="showYAxis"
  [legend]="showLegend"
  [showXAxisLabel]="showXAxisLabel"
  [showYAxisLabel]="showYAxisLabel"
  [xAxisLabel]="xAxisLabel"
  [yAxisLabel]="yAxisLabel"
  (select)="onSelect($event)">
</ngx-charts-bar-vertical>
