import { Component, OnInit } from '@angular/core';
import { Reservation } from '../models/Reservation';

@Component({
  selector: 'app-guests-per-day-chart',
  templateUrl: './guests-per-day-chart.component.html',
  styleUrls: ['./guests-per-day-chart.component.css']
})
export class GuestsPerDayChartComponent implements OnInit{

  ngOnInit(): void {
    this.initBarChart();
  }

  reservations: Reservation[] = []

  initBarChart() {
    const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    const data = days.map(day => ({
      day: day,
      guests: this.reservations.filter(reservation =>
        new Date(reservation.startTime).getDay() === days.indexOf(day) &&
        reservation.confirmedByWaiter != ''
      ).length
    }));

    this.barChartData = {
      labels: days,
      datasets: [
        {
          label: 'Number of Guests',
          backgroundColor: '#42A5F5',
          borderColor: '#1E88E5',
          data: data.map(d => d.guests)
        }
      ]
    };
  }
}
