export class WaiterComponent {
  constructor(router, userService, restaurantService) {
    this.router = router;
    this.userService = userService;
    this.restaurantService = restaurantService;
    this.page = 1;
    this.user = new User();
    this.error = "";
    this.reservations = [];
    this.pendingReservations = [];
    this.acceptedReservations = [];
    this.orders = [];
    this.barChartRes = [];
    this.allPitaUsers = [];
    this.allHistogramReservations = [];
    this.barChartPromenljiva = [];
    this.pieChartPromenljiva = [];
    this.histogramData = [];
    this.temp = {};
    this.ctx = null;
    this.objectType = '';
  }
  ngOnInit() {
    let p = localStorage.getItem("page");
    if (p) this.page = parseInt(p);
    let temp = localStorage.getItem("user");
    if (temp) {
      this.user = JSON.parse(temp);
      if (this.user.type != "waiter") {
        localStorage.removeItem("user");
        this.router.navigate([""]);
      }
      if (this.user.accountStatus != 1) {
        this.error = "This account is not active!";
        localStorage.removeItem("user");
        this.router.navigate([""]);
      }
    } else {
      this.router.navigate([""]);
    }
    this.restaurantService.getReservations().subscribe(data => {
      this.reservations = data.filter(reservation => reservation.restaurantName === this.user.worksAt).filter(reservation => {
        return reservation.cancelledByWaiter == false;
      }).sort((a, b) => {
        return a.startTime < b.startTime ? 1 : -1;
      });
      this.reservations.forEach(reservation => {
        reservation.startTime = new Date(reservation.startTime);
        reservation.endTime = new Date(reservation.endTime);
        this.restaurantService.getAvailableTables(reservation.restaurantName, reservation.startTime, reservation.endTime, reservation.numberOfPeople).subscribe(data => {
          reservation.availableTables = data;
        });
      });
      this.pendingReservations = this.reservations.filter(reservation => reservation.confirmedByWaiter === "");
      this.acceptedReservations = this.reservations.filter(reservation => reservation.confirmedByWaiter == this.user.username);
    });
    this.restaurantService.getOrders().subscribe(data => {
      this.orders = data.filter(order => order.restaurantName === this.user.worksAt).filter(order => order.status === "Pending").sort((a, b) => {
        return a.orderTime < b.orderTime ? -1 : 1;
      });
      this.orders.forEach(order => {
        order.orderTime = new Date(order.orderTime);
      });
    });
    // barchart
    this.restaurantService.getReservations().subscribe(data => {
      this.barChartRes = data.filter(reservation => reservation.restaurantName === this.user.worksAt).filter(reservation => {
        return reservation.cancelledByWaiter == false;
      }).sort((a, b) => {
        return a.startTime < b.startTime ? 1 : -1;
      });
      this.barChartRes.forEach(reservation => {
        reservation.startTime = new Date(reservation.startTime);
        reservation.endTime = new Date(reservation.endTime);
      });
      let tempReservationsForUser = this.barChartRes.filter(reservation => {
        return reservation.confirmedByWaiter == this.user.username;
      });
      let barchartData = [tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 0).length, tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 1).length, tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 2).length, tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 3).length, tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 4).length, tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 5).length, tempReservationsForUser.filter(reservation => reservation.startTime.getDay() == 6).length];
      this.barChartPromenljiva = barchartData;
      this.summonBarChart();
      // use barchartData to create barchart
    });
    //barchart end
    // pita diagram
    this.userService.getUsers().subscribe(data => {
      this.allPitaUsers = data.filter(user => user.worksAt == this.user.worksAt).filter(user => user.type == "waiter");
      let pitaDiagramData = [];
      this.allPitaUsers.forEach(pitaUser => {
        this.restaurantService.getReservations().subscribe(data => {
          let tempReservations = data.filter(reservation => reservation.confirmedByWaiter == pitaUser.username).filter(reservation => reservation.restaurantName == this.user.worksAt);
          let userData = {
            name: pitaUser.username,
            y: tempReservations.length
          };
          pitaDiagramData.push(userData);
          if (pitaDiagramData.length === this.allPitaUsers.length) {
            this.summonPieChart(pitaDiagramData);
          }
        });
      });
      // this.pieChartPromenljiva = pitaDiagramData
      // this.summonPieChart(pitaDiagramData)
    });
    // pita diagram end
    this.loadReservations();
    this.restaurantService.getRestaurants().subscribe(data => {
      const restaurant = data.find(restaurant => restaurant.name === this.user.worksAt);
      if (restaurant) {
        this.drawLayout(restaurant.layout);
      }
    });
  }
  loadReservations() {
    this.restaurantService.getReservations().subscribe(data => {
      this.allHistogramReservations = data.filter(reservation => reservation.restaurantName === this.user.worksAt).filter(reservation => reservation.showedUp === 1).sort((a, b) => a.startTime < b.startTime ? -1 : 1);
      const twentyFourMonthsAgo = moment().subtract(24, 'months').toDate();
      this.allHistogramReservations = this.allHistogramReservations.filter(reservation => new Date(reservation.startTime) > twentyFourMonthsAgo);
      const dayOfWeekMap = new Map();
      this.allHistogramReservations.forEach(reservation => {
        const dayOfWeek = new Date(reservation.startTime).getDay();
        if (!dayOfWeekMap.has(dayOfWeek)) {
          dayOfWeekMap.set(dayOfWeek, {
            count: 0,
            total: 0
          });
        }
        const dayData = dayOfWeekMap.get(dayOfWeek);
        dayData.count += 1;
        dayData.total += 1;
      });
      this.histogramData = Array.from(dayOfWeekMap.entries()).map(([day, data]) => {
        return {
          day: moment().day(day).format('ddd'),
          average: data.count / (24 * 4) // 24 months * 4 weeks/month
        };
      });

      this.renderHistogram();
    });
  }
  renderHistogram() {
    const data = this.histogramData.map(data => data.average);
    const categories = this.histogramData.map(data => data.day);
    Highcharts.chart('histogram-chart', {
      chart: {
        type: 'column'
      },
      title: {
        text: 'Average Reservations per Day of the Week'
      },
      xAxis: {
        categories: categories,
        title: {
          text: 'Day of the Week'
        }
      },
      yAxis: {
        min: 0,
        title: {
          text: 'Average Reservations'
        }
      },
      series: [{
        type: 'column',
        name: 'Average Reservations',
        data: data
      }],
      plotOptions: {
        column: {
          pointPadding: 0.2,
          borderWidth: 0
        }
      }
    });
  }
  summonBarChart() {
    const data = this.barChartPromenljiva;
    const kategorije = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    const options = {
      chart: {
        type: 'column'
      },
      title: {
        text: 'Number of guests per day for: ' + this.user.username
      },
      xAxis: {
        categories: kategorije
      },
      yAxis: {
        title: {
          text: 'Number of guests'
        }
      },
      series: [{
        type: 'column',
        name: 'Days',
        data: data
      }]
    };
    Highcharts.chart('guest-chart', options);
  }
  summonPieChart(data) {
    console.log(data);
    Highcharts.chart('pie-chart', {
      chart: {
        type: 'pie'
      },
      title: {
        text: 'Guest Distribution by Waiter'
      },
      series: [{
        type: 'pie',
        name: 'Guest Distribution',
        data: data
      }]
    });
  }
  drawLayout(layout) {
    const canvas = document.getElementById('layoutCanvas');
    const ctx = canvas.getContext('2d');
    if (!ctx) {
      return;
    }
    // Clear previous drawings
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    // Draw tables
    ctx.fillStyle = 'red';
    layout.tables.forEach(table => {
      ctx.beginPath();
      ctx.arc(table.x, table.y, table.radius, 0, Math.PI * 2);
      ctx.fill();
      ctx.fillStyle = 'black';
      ctx.fillText(`Table ${table.id}`, table.x - 20, table.y);
    });
    // Draw kitchens
    ctx.fillStyle = 'green';
    layout.kitchens.forEach(kitchen => {
      ctx.fillRect(kitchen.x, kitchen.y, kitchen.width, kitchen.height);
      ctx.fillStyle = 'black';
      ctx.fillText(`Kitchen ${kitchen.id}`, kitchen.x, kitchen.y - 10);
    });
    // Draw toilets
    ctx.fillStyle = 'purple';
    layout.toilets.forEach(toilet => {
      ctx.fillRect(toilet.x, toilet.y, toilet.width, toilet.height);
      ctx.fillStyle = 'black';
      ctx.fillText(`Toilet ${toilet.id}`, toilet.x, toilet.y - 10);
    });
  }
