import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-pocetna',
  templateUrl: './pocetna.component.html',
  styleUrls: ['./pocetna.component.css']
})
export class PocetnaComponent {

  constructor(private userService: UserService, private router: Router)
  {

  }

  username: string = ""
  password: string = ""
  type: string = ""
  error: string = ""


  login()
  {
    if (this.username == "" || this.password == "" || this.type == "")
    {
      this.error = "Unesite sve podatke!";
      return;
    }

    this.userService.login(this.username, this.password, this.type).subscribe((data: { type: any; }) =>{
      if (data)
      {
        this.error = ""
        localStorage.setItem("user", JSON.stringify(data))
        this.router.navigate([data.type]);
      }
      else
      {
        this.error = "Nepostojeci korisnik"
      }
    })
  }

}
<h2>Kutak dobre hrane</h2>


Login<br>

username: <input type="text" name="username" [(ngModel)]="username"><br>
password: <input type="text" name="password" [(ngModel)]="password"><br>

Tip: <br>
<input type="radio" value="kupac" [(ngModel)]="tip">Kupac<br>
<input type="radio" value="prodavac" [(ngModel)]="tip">Prodavac<br><br>

<button (click)="login()">Login</button><br>

{{this.error}}
