import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-guests-per-day-chart',
  templateUrl: './guests-per-day-chart.component.html',
  styleUrls: ['./guests-per-day-chart.component.css']
})
export class GuestsPerDayChartComponent implements OnInit{

  ngOnInit(): void {

  }

  initBarChart() {
    const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    const data = days.map(day => ({
      day: day,
      guests: this.reservations.filter(reservation =>
        new Date(reservation.reservation_datetime).getDay() === days.indexOf(day) &&
        reservation.status !== 'Denied'
      ).length
    }));

    this.barChartData = {
      labels: days,
      datasets: [
        {
          label: 'Number of Guests',
          backgroundColor: '#42A5F5',
          borderColor: '#1E88E5',
          data: data.map(d => d.guests)
        }
      ]
    };
  }
}
<div class="chart-container">
    <h2>Number of Guests per Day</h2>
    <div class="chart">
      <div class="bar monday" [style.height.px]="mondayGuests"></div>
      <div class="bar tuesday" [style.height.px]="tuesdayGuests"></div>
      <div class="bar wednesday" [style.height.px]="wednesdayGuests"></div>
      <div class="bar thursday" [style.height.px]="thursdayGuests"></div>
      <div class="bar friday" [style.height.px]="fridayGuests"></div>
      <div class="bar saturday" [style.height.px]="saturdayGuests"></div>
      <div class="bar sunday" [style.height.px]="sundayGuests"></div>
    </div>
  </div>
