import { Component } from '@angular/core';
import { User } from '../models/User';
import { Router } from '@angular/router';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-waiter',
  templateUrl: './waiter.component.html',
  styleUrls: ['./waiter.component.css']
})
export class WaiterComponent {
  constructor(private router:Router, private userService: UserService)
  {

  }

  ngOnInit(): void {
    let temp = localStorage.getItem("user")
    if(temp)
    {
      this.user = JSON.parse(temp);

      if (this.user.type != "waiter")
      {
        localStorage.removeItem("user")
        this.router.navigate([""]);
      }

        if (this.user.accountStatus != 1)
        {
          this.error = "This account is not active!"
          localStorage.removeItem("user")
          this.router.navigate([""]);
        }

    }
    else
    {
      this.router.navigate([""]);
    }
  }

  page: number = 1
  user: User = new User()
  error: string = ""

  navigateTo(newPage: number)
  {
    this.page = newPage
  }

  updateInfo()
  {
    if (!this.user.forename || !this.user.surname || !this.user.address || !this.user.contactPhone ||
      !this.user.email || !this.user.creditCardNumber) {
      this.error = "Fields can not be empty!";
      return;
    }

    this.userService.updateInfo(this.user.username, this.user.forename, this.user.surname, this.user.address, this.user.email, 
      this.user.contactPhone, this.user.creditCardNumber).subscribe((data:any) => {
        if (data.msg === "User information updated successfully!")
        {
          localStorage.setItem("user", JSON.stringify(this.user))
        }

        alert(data.msg)
        window.location.reload();
    })
  }

  logout()
  {
    localStorage.removeItem("user")
    this.router.navigate([""])
  }
}
<nav class="navbar navbar-expand-lg navbar-dark">
    <div class="container">
      <a class="navbar-brand" href="#">Kutak dobre hrane</a>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav ml-auto">
          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(1)">Profile</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(2)">Reservations</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(3)">Food delivery</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(4)">Statistics</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="logout()">Log out</a>
          </li>
        </ul>
      </div>
    </div>
</nav>

<div class="container">

    <div *ngIf="page == 1">
      <h3>Profile</h3><br>

        Forename:<br>
        <input type="text" name="forename" placeholder="Forename" [(ngModel)]="user.forename">

        Surname:<br>
        <input type="text" name="surname" placeholder="Surname" [(ngModel)]="user.surname">

        Address:<br>
        <input type="text" name="address" placeholder="Address" [(ngModel)]="user.address">

        Email:<br>
        <input type="email" name="email" placeholder="Email" [(ngModel)]="user.email">

        Contact phone:<br>
        <input type="tel" name="contactPhone" placeholder="Phone" [(ngModel)]="user.contactPhone">

        Credit card number:<br>
        <input type="number" name="creditCardNumber" placeholder="Credit Card Number" [(ngModel)]="user.creditCardNumber">

        <p class="error">{{error}}</p>
        <br>
        <button (click)="updateInfo()">Update info</button>
    </div>

    <div *ngIf="page == 2">
        <h3>Restaurants</h3><br>



</div>
