import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { User } from '../models/User';
import { UserService } from '../services/user.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit{

  constructor(private router: Router, private userService: UserService)
  {

  }

  ngOnInit(): void {
    let temp = localStorage.getItem("user")
    if(temp)
    {
      this.admin = JSON.parse(temp);

      if (this.admin.type != "admin")
      {
        localStorage.removeItem("user")
        this.router.navigate([""]);
      }
    }
    else
    {
      this.router.navigate([""]);
    }

    // page 1 - list all users
    this.userService.getUsers().subscribe(data => {
      if (data)
      {
        this.users = data
      }

    })
  }

  page: number = 1
  users: User[] = []
  admin: User = new User()

  navigateTo(newPage: number)
  {
    this.page = newPage
  }

  updateInfo(user:User)
  {
    if (!user.forename || !user.surname || !user.address || !user.contactPhone ||
      !user.email || !user.creditCardNumber) {
      alert("Fields can not be empty!");
      return;
    }

    this.userService.updateInfo(user.username, user.forename, user.surname, user.address, user.email, 
      user.contactPhone, user.creditCardNumber).subscribe((data:any) => {
        alert(data.msg)
        window.location.reload();
    })
  }

  logout()
  {
    localStorage.removeItem("user")
    this.router.navigate([""])
  }
}
<nav class="navbar navbar-expand-lg navbar-dark">
    <div class="container">
      <a class="navbar-brand" href="#">Kutak dobre hrane</a>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav ml-auto">
          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(1)">User List</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(2)">Restaurant List</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(3)">User Requests</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(4)">Add Restaurant</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(5)">Add Waiter</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="logout()">Log out</a>
          </li>
        </ul>
      </div>
    </div>
</nav>

<div class="container">

    <div *ngIf="page == 1">
        <h3>User List</h3><br>

        <table>
            <tr>
                <th>Forename</th>
                <th>Forename</th>
                <th>Surname</th>
                <th>Address</th>
                <th>Email</th>
                <th>Contact Phone</th>
                <th>Credit Card Number</th>
            </tr>

            <tr *ngFor="let user of users">
                <input type="text" name="forename" placeholder="Forename" [(ngModel)]="user.forename">

                <input type="text" name="surname" placeholder="Surname" [(ngModel)]="user.surname">

                <input type="text" name="address" placeholder="Address" [(ngModel)]="user.address">

                <input type="email" name="email" placeholder="Email" [(ngModel)]="user.email">

                <input type="tel" name="contactPhone" placeholder="Phone" [(ngModel)]="user.contactPhone">

                <input type="number" name="creditCardNumber" placeholder="Credit Card Number" [(ngModel)]="user.creditCardNumber">

                <button (click)="updateInfo(user)">Save</button>

                <button >Ban</button>
            </tr>
        </table>
        <br>
    </div>

</div>
