import { Component, OnInit } from '@angular/core';
import { User } from '../models/User';
import { Router } from '@angular/router';

@Component({
  selector: 'app-guest',
  templateUrl: './guest.component.html',
  styleUrls: ['./guest.component.css']
})
export class GuestComponent implements OnInit {

  constructor(private router:Router)
  {

  }

  ngOnInit(): void {
    let temp = localStorage.getItem("user")
    if(temp)
    {
      this.user = JSON.parse(temp);
    }
    else
    {
      this.router.navigate([""]);
    }
  }

  page: number = 1
  user: User = new User()

  navigateTo(newPage: number)
  {
    this.page = newPage
  }
}
<nav class="navbar navbar-expand-lg navbar-dark">
    <div class="container">
      <a class="navbar-brand" href="#">Kutak dobre hrane</a>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav ml-auto">
          <li class="nav-item">
            <a class="nav-link" href="#" (click)="navigateTo(0)">Profile</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" href="#" (click)="navigateTo(1)">Restaurants</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" href="#" (click)="navigateTo(2)">Reservations</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" href="#" (click)="navigateTo(3)">Food delivery</a>
          </li>
        </ul>
      </div>
    </div>
</nav>

<div class="container">

    <div *ngIf="page == 1">
      <h4>Profile</h4><br>


    </div>



</div>
