import { Component, OnInit } from '@angular/core';
import { User } from '../models/User';
import { Router } from '@angular/router';
import { UserService } from '../services/user.service';
import { Restaurant } from '../models/Restaurant';
import { RestaurantService } from '../services/restaurant.service';
import { Reservation } from '../models/Reservation';

@Component({
  selector: 'app-guest',
  templateUrl: './guest.component.html',
  styleUrls: ['./guest.component.css']
})
export class GuestComponent implements OnInit {

  constructor(private router:Router, private userService: UserService, private restaurantService: RestaurantService)
  {

  }

  ngOnInit(): void {
    let p = localStorage.getItem("page")
    if (p)
      this.page = parseInt(p)

    let temp = localStorage.getItem("user")
    if(temp)
    {
      this.user = JSON.parse(temp);

      if (this.user.type != "guest")
      {
        localStorage.removeItem("user")
        this.router.navigate([""]);
      }
      if (this.user.accountStatus != 1)
      {
        alert("This account is not active!")
        localStorage.removeItem("user")
        this.router.navigate([""]);
      }
    }
    else
    {
      this.router.navigate([""]);
    }

    this.restaurantService.getRestaurants().subscribe(data => {
      this.restaurants = data
    })



    this.userService.getUsers().subscribe(data => {
      this.waiters = data.filter(elem => {
        if (elem.type == "waiter")
          return true
        return false
      })
    })

    this.userService.getReservations(this.user.username).subscribe(data => {
      for (let i = 0; i < data.length; i++)
      {
        data[i].startTime = new Date(data[i].startTime)
        data[i].endTime = new Date(data[i].endTime)
      }

      this.activeReservations = data.filter(elem => {
        if (new Date(elem.endTime) > new Date())
          return true
        return false
      }).sort((a, b) => {
        return new Date(a.startTime) < new Date(b.startTime) ? -1 : 1
      })
      this.expiredReservations = data.filter(elem => {
        if (new Date(elem.endTime) < new Date())
          return true
        return false
      }).sort((a, b) => {
        return new Date(a.startTime) < new Date(b.startTime) ? -1 : 1
      })
    })
  }

  restaurantDetails(restaurant: Restaurant)
  {
    localStorage.setItem("restaurant", JSON.stringify(restaurant))

    this.router.navigate(["restaurantInformation"]);
  }

  page: number = 1
  user: User = new User()
  error: string = ""

  restaurants: Restaurant[] = []
  waiters: User[] = []

  sortColumn: keyof Restaurant = 'name';
  sortOrder: 'asc' | 'desc' = 'asc';

  searchName: string = '';
  searchAddress: string = '';
  searchType: string = '';

  expiredReservations: Reservation[] = []
  activeReservations: Reservation[] = []

  navigateTo(newPage: number)
  {
    this.page = newPage
    localStorage.setItem("page", newPage.toString())
  }

  updateInfo()
  {
    if (!this.user.forename || !this.user.surname || !this.user.address || !this.user.contactPhone ||
      !this.user.email || !this.user.creditCardNumber) {
      this.error = "Fields can not be empty!";
      return;
    }

    this.userService.updateInfo(this.user.username, this.user.forename, this.user.surname, this.user.address, this.user.email, 
      this.user.contactPhone, this.user.creditCardNumber).subscribe((data:any) => {
        if (data.msg === "User information updated successfully!")
        {
          localStorage.setItem("user", JSON.stringify(this.user))
        }

        alert(data.msg)
        window.location.reload();
    })
  }

  get sortedAndFilteredRestaurants(): Restaurant[] {
    return this.restaurants
      .filter(restaurant => 
        restaurant.name.toLowerCase().includes(this.searchName.toLowerCase()) &&
        restaurant.address.toLowerCase().includes(this.searchAddress.toLowerCase()) &&
        restaurant.type.toLowerCase().includes(this.searchType.toLowerCase())
      )
      .sort((a, b) => {
        const valueA = a[this.sortColumn];
        const valueB = b[this.sortColumn];
        let comparison = 0;
        if (valueA > valueB) {
          comparison = 1;
        } else if (valueA < valueB) {
          comparison = -1;
        }
        return this.sortOrder === 'asc' ? comparison : -comparison;
      });
  }

  setSort(column: keyof Restaurant): void {
    if (this.sortColumn === column) {
      this.sortOrder = this.sortOrder === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortColumn = column;
      this.sortOrder = 'asc';
    }
  }


  isCancelButtonDisabled(reservation: Reservation): boolean {
  const expirationTime = new Date(reservation.startTime.getTime() + (45 * 60000)); // Adding 45 minutes to the start time
  //return expirationTime > new Date() || reservation.cancelledByUser == true || reservation.cancelledByWaiter == true;
    return false;
  }

  findRestaurantAddress(restaurantName: string): string {
    const restaurant = this.restaurants.find(restaurant => restaurant.name === restaurantName);
    return restaurant?.address ?? '';
  }

  cancelReservation(reservation: Reservation)
  {
    if (this.isCancelButtonDisabled(reservation)) {
      alert("You can't cancel reservation 45 minutes before it starts!")
      return;
    }

    if(reservation.cancelledByUser == true)
    {
        alert("This reservation has already been cancelled!")
        return;
    }

    if (confirm("Are you sure you want to cancel this reservation?"))
    {
      this.userService.cancelReservation(reservation).subscribe((data:any) => {
        alert(data.msg)
        window.location.reload();
      })
    }

  }


  logout()
  {
    localStorage.removeItem("page")
    localStorage.removeItem("user")
    this.router.navigate([""])
  }
}
<nav class="navbar navbar-expand-lg navbar-dark">
    <div class="container">
      <a class="navbar-brand" href="#">Kutak dobre hrane</a>
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNav">
        <ul class="navbar-nav ml-auto">
          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(1)">Profile</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(2)">Restaurants</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(3)">Reservations</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="navigateTo(4)">Food delivery</a>
          </li>

          <li class="nav-item">
            <a class="nav-link" (click)="logout()">Log out</a>
          </li>
        </ul>
      </div>
    </div>
</nav>

<div class="container">

    <div *ngIf="page == 1">
      <h3>Profile</h3><br>

        Forename:<br>
        <input type="text" name="forename" placeholder="Forename" [(ngModel)]="user.forename">

        Surname:<br>
        <input type="text" name="surname" placeholder="Surname" [(ngModel)]="user.surname">

        Address:<br>
        <input type="text" name="address" placeholder="Address" [(ngModel)]="user.address">

        Email:<br>
        <input type="email" name="email" placeholder="Email" [(ngModel)]="user.email">

        Contact phone:<br>
        <input type="tel" name="contactPhone" placeholder="Phone" [(ngModel)]="user.contactPhone">

        Credit card number:<br>
        <input type="number" name="creditCardNumber" placeholder="Credit Card Number" [(ngModel)]="user.creditCardNumber">

        <p class="error">{{error}}</p>
        <br>
        <button (click)="updateInfo()">Update info</button>
    </div>

    <div *ngIf="page == 2">
        <h3>Restaurants</h3><br>

        <div>
          <label>
            Name:
            <input type="text" [(ngModel)]="searchName" />
          </label>
          <label>
            Address:
            <input type="text" [(ngModel)]="searchAddress" />
          </label>
          <label>
            Type:
            <input type="text" [(ngModel)]="searchType" />
          </label>
        </div>

        <table>
          <thead>
            <tr>
              <th (click)="setSort('name')">Name</th>
              <th (click)="setSort('address')">Address</th>
              <th (click)="setSort('type')">Type</th>
              <th>Workers</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let restaurant of sortedAndFilteredRestaurants">
              <td (click)="restaurantDetails(restaurant)">{{ restaurant.name }}</td>
              <td>{{ restaurant.address }}</td>
              <td>{{ restaurant.type }}</td>
              <td>
                <ul>
                  <div *ngFor="let waiter of waiters">
                    <div *ngIf="waiter.worksAt == restaurant.name">
                      {{waiter.forename}} {{waiter.surname}}
                    </div>
                  </div>
                </ul>
              </td>
            </tr>
          </tbody>
        </table>
    </div>

    <div *ngIf="page == 3">
      <h3>Active Reservations</h3><br>

      <div *ngIf="activeReservations.length == 0">
        <p>No active reservations</p>
      </div>
      <div *ngIf="activeReservations.length != 0">
        <table>
          <tr>
              <th>Restaurant</th>
              <th>Address</th>
              <th>Date</th>
              <th>Time</th>
              <th>Additional Requests</th>
              <th>Cancel Reservation</th>
          </tr>
          <tr *ngFor="let reservation of activeReservations">
              <td>
                  <p>{{reservation.restaurantName}}</p>
              </td>
              <td>
                <p>{{findRestaurantAddress(reservation.restaurantName)}}</p>
              </td>
              <td>
                  <p>{{reservation.startTime.getDate()}}.{{reservation.startTime.getMonth() + 1}}</p>
              </td>
              <td>
                  <p>{{reservation.startTime.getHours()}}:{{reservation.startTime.getMinutes().toString().padStart(2, '0')}} - {{reservation.endTime.getHours()}}:{{reservation.endTime.getMinutes().toString().padStart(2, '0')}}</p>
              </td>
              <td>
                  <p>{{reservation.additionalRequests}}</p>
              </td>
              <td>
                <button (click)="cancelReservation(reservation)" disabled=isCancelButtonDisabled(reservation)>Cancel</button>
            </td>
          </tr>
        </table>
      </div>

    <br>

    <h3>Expired Reservations</h3><br>

      <div *ngIf="expiredReservations.length == 0">
        <p>No expired reservations</p>
      </div>
      <div *ngIf="expiredReservations.length != 0">
        <table>
          <tr>
              <th>Restaurant</th>
              <th>Address</th>
              <th>Date</th>
              <th>Time</th>
              <th>Additional Requests</th>
          </tr>
          <tr *ngFor="let reservation of expiredReservations">
              <td>
                  <p>{{reservation.restaurantName}}</p>
              </td>
              <td>
                <p>{{findRestaurantAddress(reservation.restaurantName)}}</p>
              </td>
              <td>
                  <p>{{reservation.startTime.getDate()}}.{{reservation.startTime.getMonth() + 1}}</p>
              </td>
              <td>
                  <p>{{reservation.startTime.getHours()}}:{{reservation.startTime.getMinutes().toString().padStart(2, '0')}} - {{reservation.endTime.getHours()}}:{{reservation.endTime.getMinutes().toString().padStart(2, '0')}}</p>
              </td>
              <td>
                  <p>{{reservation.additionalRequests}}</p>
              </td>
          </tr>
        </table>
      </div>
    </div>
</div>
