import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor() { }

  login(username: string, password: string, tip: string)
  {
    const data= {
      username: username,
      password: password,
      tip: tip,
    }

    return this.http.post<User>('http://localhost:4000/users/login', data);
  }
}
